#!/usr/bin/env node
const [, , ...args] = process.argv;
var readJson = require('read-package-json');
var fs = require('fs');
var currentDir = process.cwd();
var Graph = require("./graph");
var mygraph = new Graph();
var exec = require('child_process').exec;

var readPackageJson = function (path) {
    return new Promise((resolve, reject) => {
        readJson(path + '/package.json', console.error, false, async function (er, data) {
            if (er) {
                console.log(er);
                console.error("There was an error reading the package.json file")
                return
            }
            else {
                resolve(data);
               // console.log('the package data is', data);
            }
        });
    });
}

var updateDependency = async(path,package)=>{
    return new Promise((resolve,reject)=>{
        let command = "cd "+ path + " && npm install --save "+ package.child.split('@')[0] + " && git add . && git commit -m updatedDependency" + package.child +" && git push";
        exec(command,(error,stdout,stderr)=>{
            if(error || stderr){
                console.log(error || stderr);
            }
            console.log(command);
            resolve();
        });
    })
}

var updatePackage = async (path,package) => {
    return new Promise(async (resolve,reject)=>{
        if(package.child!=null){
            await updateDependency(path,package);
        }
        let command="cd "+path +" && npm version "
        if (args[1] == 'patch') {
            command = command + "patch";
        }
        else if (args[1] == 'minor') {
            command = command + "minor";
        }
        else if (args[1] == 'major') {
            command = command + "major";
        }
        else {
            console.log("invalid command");
            return;
        }
        if(package.child == null){
            command = command + ' -m "Updated '+ args[1] +' version"';
        }
        else{
            let packageJSON = await readPackageJson(path);
            let gitURL = packageJSON.repository.url.split('+')[1];
            console.log(gitURL);
            let commandgit = "cd " + path + " && git init && git remote add origin "+ gitURL;
            // + " && git checkout master" ;
            command = command + ' -m "Updated due to '+ args[1] +' update on a dependency' + package.child + '"';
            command = commandgit + ' && ' + command;
        }
        exec(command,(error,stdout,stderr)=>{
            if(error || stderr){
                console.log(error||stderr);
                exec("rm -r temptushar"+package.parent,(error,stdout,stderr)=>{
                    console.log("deleted");
                    resolve();
                    })    
            }
            else{
                console.log("successful")
                exec("rm -r temptushar"+package.parent,(error,stdout,stderr)=>{
                console.log("deleted");
                resolve();
                })    
            }
        })
    });
}


