var semver  = require('semver')

exports.versionIncrementer = (version,type)=>{
        version = version.split('|')[version.split('|').length-1].trim();
        let adder = "";
        if(version[0] == '^' || version[0] == '~')
        {
            adder = version[0];
            version = version.slice(1,version.length);
        }
        while(version.split('.').length < 3){
            version = version + '.0';
        }
        return(adder + semver(version,type));
 }